    <script type="text/javascript">
        jQuery('.switcher .selected').click(function() {
            console.log('.switcher .selected click')
                jQuery('.switcher .option a img').each(function() {
                    if(!jQuery(this)[0].hasAttribute('src'))jQuery(this).attr('src', jQuery(this).attr('data-gt-lazy-src'))
                });
                if(!(jQuery('.switcher .option').is(':visible'))) {
                    jQuery('.switcher .option').stop(true,true).delay(100).slideDown(500);jQuery('.switcher .selected a').toggleClass('open')
                }
        });
        jQuery('.switcher .option').bind('mousewheel', function(e) {
            console.log('.switcher .option click')
            var options = jQuery('.switcher .option');
                if(options.is(':visible'))options.scrollTop(options.scrollTop() - e.originalEvent.wheelDelta);
            return false;
        });
        jQuery('body').not('.switcher').click(function(e) {
            jQuery('.switcher .option a img').each(function() {
                    if(!jQuery(this)[0].hasAttribute('src'))jQuery(this).attr('src', jQuery(this).attr('data-gt-lazy-src'))
                });
                if(!(jQuery('.switcher .option').is(':visible'))) {
                    jQuery('.switcher .option').stop(true,true).delay(100).slideDown(500);jQuery('.switcher .selected a').toggleClass('open')
                }
                
            console.log('body not .switcher click')
            if(jQuery('.switcher .option').is(':visible') && e.target != jQuery('.switcher .option').get(0)) {

                console.log('body not .switcher if cond ')
                jQuery('.switcher .option').stop(true,true).delay(100).slideUp(500);
            }
        });
    </script>
